INSERT INTO `field_revision_field_foa_description` VALUES ('node','funded_opportunity','0','13','13','und','0','This FOA supports the advanced development and enhancement of emerging informatics technologies to improve the acquisition, management, analysis, and dissemination of data and knowledge in support of cancer research.',null),('node','funded_opportunity','0','14','14','und','0','This FOA supports the development of innovative methods and algorithms in biomedical computing, informatics, and data science addressing priority needs across the cancer research continuum.',null),('node','funded_opportunity','0','54','54','und','0','This FOA supports the development of enabling informatics technologies to improve the acquisition, management, analysis, and dissemination of data and knowledge in support of cancer research.',null),('node','funded_opportunity','0','55','55','und','0','This FOA supports the continued development and sustainment of high-value informatics research resources to serve current and emerging needs across the cancer research continuum. ',null);
